
package week1exercise.day2;

public class CastExample
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		
		
		short ss = 23;
		byte b = (byte) ss;
		
		System.out.println(b);
		
		double db = 12.55;
		float ff = (float) db;
		long ll = (long) db;
		System.out.println(ff);
		System.out.println(ll);
	}

}
